sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	var newDate = newValue;

    var checkDateGA = new GlideAjax('HBCatalogAjax');
    checkDateGA.addParam('sysparm_name', 'compareDates');
    checkDateGA.addParam('sysparm_compare', newDate);
    checkDateGA.getXMLAnswer(function(answer) {

		// if the estimated delivery date is earlier than current date, clear the value and show error message
        if (answer < 0) {
            g_form.setValue('estimated_delivery', '');
            g_form.showFieldMsg('estimated_delivery', getMessage('hb_request_delivery_date_validation_failed'), 'error', true);
        }
    });

}
